<template>
    <div
        id="verify"
        class="mainContent"
        :style="{backgroundImage: `url(${require('@/assets/images/verify-background.ed583cf.jpg')})`}"
    >
        <div id="wrapper1">
            <div id="title"></div>
            <div id="checkerHolder">
                <p id="subTitle"><span id="text1">域名认证</span> <span id="text2">提示：请在下方输入域名，验证是否为我公司官方域名</span></p>
                <div id="checker">
                    <div id="inputWrapper">
                        <span id="placeHolder">http://</span>
                        <input
                            type="text"
                            name="checkerInput"
                            id="checkerInput"
                            autofocus="autofocus"
                            v-model="urlVal"
                            autocomplete="off"
                        >
                    </div>
                    <input
                        @click="get"
                        type="submit"
                        name="checkerButton"
                        id="checkerButton"
                        value="验证"
                    >
                </div>
                <p
                    id="checkerStatus"
                    :class="tipClass"
                >{{tipText}}</p>
                <!-- class="no_input green" -->
            </div>
        </div>
    </div>
</template>

<script>
export default {
    data() {
        return {
            urlVal: "",
            tipText: "",
            tipClass: "no_input green"
        };
    },
    methods: {
        get() {
            const xx =
                "188hg1.com|188hg2.com|188hg3.com|188hg4.com|188hg5.com|188hg6.com|188hg7.com|188hg8.com|188hg9.com|www188hg.com|www-188hg.com|188hg.bet|188hg.com|188hg.cc|vip188hg.com|188188hg.com|188mq.com|188by.com|188cr.com|0088mq.com|ra199.com|ra600.com|hg0088mq.com|1717mq.com|ra199.cc|ra600.cc|188hg.net|188hg.cc|6686mq.com|rr58.com|188hg.co|188hg.biz|188hg.org|188hg.me|188pk.cc|188188001.com|188188002.com|";
            const x = xx.split("|");
            let yzz;
            yzz = this.urlVal;
            if (yzz == "") {
                this.tipText = "请输入要检测的域名";
                this.tipClass = "class no_input";
                return;
            }
            let zz = false;
            for (let i = 0; i < x.length; i++) {
                if (x[i] == yzz || "www." + x[i] == yzz) {
                    zz = true;
                    break;
                }
            }
            if (zz) {
                this.tipText = "此域名通过认证，欢迎访问我公司网站";
                this.tipClass = "class no_input green";
            } else {
                this.tipText = "此域名没有通过认证，请谨慎访问";
                this.tipClass = "class no_input red";
            }
        }
    }
};
